"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[223],{3223:function(n){n.exports=JSON.parse('{"title":"Đồ ăn thức uống tại Việt Nam","north":"M\xf3n ăn miền Bắc","south":"M\xf3n ăn Nam Bộ","central":"M\xf3n ăn miền Trung","beverage1":"Tr\xe0 sen","beverage1Explain":"Một loại tr\xe0 xanh c\xf3 hương hoa sen. Thường được coi l\xe0 loại tr\xe0 Việt Nam danh tiếng nhất, n\xf3 được sản xuất ở quy m\xf4 nhỏ trong một quy tr\xecnh tốn nhiều c\xf4ng sức v\xe0 thời gian hiếm thấy hiện nay.","beverage2":"Rượu gạo","beverage2Explain":"Rượu gạo Việt Nam được biết đến dưới nhiều t\xean gọi kh\xe1c nhau, nhưng th\xf4ng thường, v\xe0 hơi nhầm lẫn, được gọi l\xe0 rượu - một thuật ngữ cũng được sử dụng cho c\xe1c loại đồ uống c\xf3 cồn kh\xe1c. Hai loại ch\xednh bao gồm rượu gạo hoặc rượu đế, l\xe0 loại rượu gạo chưng cất thường được ng\xe2m với tr\xe1i c\xe2y v\xe0 rượu cần, một loại rượu l\xean men thường được l\xe0m trong vại đất s\xe9t v\xe0 uống bằng ống h\xfat.","beverage3":"C\xe0 Ph\xea Trứng","beverage3Explain":"Một loại đồ uống ngọt v\xe0 đậm đặc của Việt Nam, theo truyền thống bao gồm c\xe0 ph\xea vối đen đậm đặc được trộn với l\xf2ng đỏ trứng v\xe0 sữa đặc c\xf3 đường. L\xf2ng đỏ trứng v\xe0 sữa được đ\xe1nh b\xf4ng trong khoảng 10 ph\xfat v\xe0 đun s\xf4i cho đến khi c\xf3 độ sệt giống như b\xe1nh trứng đường.","beverage4":"C\xe0 Ph\xea Đ\xe1","beverage4Explain":"Thức uống kết hợp giữa c\xe0 ph\xea đậm đặc, sữa đặc v\xe0 đ\xe1. Theo truyền thống, n\xf3 được pha bằng c\xe0 ph\xea xay vừa hoặc th\xf4 do Việt Nam trồng, điển h\xecnh l\xe0 giống Robusta, được pha bằng phin nhỏ giọt—trong đ\xf3 c\xe0 ph\xea được pha v\xe0 sau đ\xf3 nhỏ giọt từ từ v\xe0o cốc.","food1":"Phở","food1Explain":"Một m\xf3n s\xfap Việt Nam bao gồm nước d\xf9ng, b\xe1nh phở, rau thơm v\xe0 thịt.","food2":"B\xe1nh m\xec","food2Explain":"Một chiếc b\xe1nh m\xec baguette ngắn với lớp vỏ mỏng, gi\xf2n v\xe0 kết cấu mềm, tho\xe1ng. N\xf3 thường được chia theo chiều dọc v\xe0 chứa đầy c\xe1c nguy\xean liệu mặn như b\xe1nh sandwich t\xe0u ngầm v\xe0 được phục vụ như một bữa ăn","food3":"B\xfan chả","food3Explain":"Một m\xf3n ăn cổ điển của miền Bắc Việt Nam. B\xfan chả c\xf3 thịt vi\xean đậm đ\xe0 v\xe0 ngon ngọt, b\xfan, nhiều loại rau thơm tươi m\xe1t v\xe0 nước mắm chanh truyền thống.","food4":"Gỏi cuốn","food4Explain":"Gỏi cuốn hay nem cuốn, gỏi cuốn, gỏi cuốn, chả gi\xf2 tươi, chả gi\xf2, b\xe1nh tr\xe1ng cuốn, l\xe0 một m\xf3n ăn truyền thống của Việt Nam bao gồm thịt lợn, t\xf4m, rau, b\xfan (b\xfan) v\xe0 c\xe1c th\xe0nh phần kh\xe1c được g\xf3i trong b\xe1nh tr\xe1ng Việt Nam (thường được gọi l\xe0 b\xe1nh tr\xe1ng hoặc b\xe1nh tr\xe1ng nguội)","northFood":"10 m\xf3n ăn miền Bắc","northFoodLink":"https://www.tasteatlas.com/most-popular-food-in-northern-vietnam","northFood1":"B\xe1nh đậu xanh","northFood1Explain":"M\xf3n tr\xe1ng miệng truyền thống của Việt Nam n\xe0y c\xf3 nguồn gốc từ Hải Dương. N\xf3 kết hợp đậu xanh, dầu thực vật hoặc mỡ lợn, đường v\xe0 hương liệu để tạo ra một chiếc b\xe1nh c\xf3 độ đặc giống như kẹo mềm v\xe0 kết cấu mịn","northFood2":"B\xfan ốc","northFood2Explain":"M\xf3n s\xfap ốc thơm n\xe0y l\xe0 một m\xf3n ăn ch\xednh ở H\xe0 Nội. N\xf3 thường được chế biến với ốc nước ngọt được kết hợp với nước d\xf9ng l\xe0m từ c\xe0 chua. M\xf3n ăn c\xf2n c\xf3 b\xfan gạo v\xe0 c\xe1c loại rau thơm tươi của Việt Nam, trong khi c\xe1c m\xf3n ăn k\xe8m đặc trưng bao gồm chả c\xe1, đậu hũ chi\xean, h\xe0nh l\xe1 th\xe1i l\xe1t, hẹ t\xe2y chi\xean hoặc ng\xf2 r\xed.","northFood3":"Cơm lam","northFood3Explain":"Cơm lam được chuẩn bị bằng c\xe1ch nhồi gạo nếp v\xe0o ống tre, đầu ti\xean được bọc trong l\xe1 chuối v\xe0 sau đ\xf3 nướng tr\xean than n\xf3ng. Nguồn gốc của n\xf3 thường gắn liền với miền Bắc v\xe0 T\xe2y Bắc Việt Nam, đặc biệt l\xe0 ở Sapa","northFood4":"B\xfan đậu mắm t\xf4m","northFood4Explain":"M\xf3n ăn truyền thống của Việt Nam n\xe0y bao gồm b\xfan gạo, đậu phụ chi\xean v\xe0 mắm t\xf4m l\xean men. Tất cả c\xe1c yếu tố được phục vụ ri\xeang biệt, c\xf9ng với một đống c\xe1c loại thảo mộc tươi xanh. N\xf3 chủ yếu gắn liền với miền Bắc Việt Nam, v\xe0 n\xf3 đặc biệt phổ biến ở H\xe0 Nội.","northFood5":"G\xe0 luộc","northFood5Explain":"Một m\xf3n ăn truyền thống được l\xe0m từ sự kết hợp của cả con g\xe0, gừng, muối, h\xe0nh l\xe1, nghệ v\xe0 đ\xf4i khi l\xe0 những sợi l\xe1 chanh","northFood6":"B\xe1nh tr\xf4i","northFood6Explain":"B\xe1nh tr\xf4i l\xe0 t\xean gọi của một m\xf3n tr\xe1ng miệng truyền thống của Việt Nam, đặc biệt l\xe0 khu vực H\xe0 Nội bao gồm những vi\xean gạo nếp nhỏ xinh thơm ngon bao quanh một lớp nh\xe2n ngọt ng\xe0o.","northFood7":"Cơm ch\xe1y","northFood7Explain":"Cơm ch\xe1y l\xe0 m\xf3n ăn truyền thống của Việt Nam v\xe0 l\xe0 đặc sản của Ninh B\xecnh. M\xf3n ăn được l\xe0m từ gạo hấp được cắt th\xe0nh h\xecnh tr\xf2n v\xe0 phẳng.","northFood8":"Cơm chi\xean","northFood8Explain":"Cơm chi\xean l\xe0 m\xf3n cơm chi\xean của người Việt Nam. M\xf3n ăn n\xe0y rất linh hoạt v\xec hầu hết mọi thứ đều c\xf3 thể được th\xeam v\xe0o cơm để l\xe0m phong ph\xfa m\xf3n ăn, nhưng n\xf3 thường được chế biến với cơm trắng, tỏi, muối v\xe0 ti\xeau. Cơm được nấu ch\xedn v\xe0 sau đ\xf3 được x\xe0o trong dầu n\xf3ng","northFood9":"B\xe1nh cuốn","northFood9Explain":"B\xe1nh cuốn l\xe0 một m\xf3n ăn đường phố phổ biến của Việt Nam bao gồm b\xe1nh cuốn hấp nh\xe2n thịt lợn, nấm hoặc cả hai. M\xf3n ăn thường được phục vụ với hẹ x\xe0o, nước mắm chua ngọt, nhiều loại rau sống v\xe0 nước sốt chanh.","northFood10":"B\xfan chả","northFood10Explain":"B\xfan chả l\xe0 một m\xf3n ăn từ thịt lợn v\xe0 b\xfan c\xf3 mối li\xean hệ phức tạp với H\xe0 Nội. M\xf3n ăn kết hợp ba yếu tố: một b\xe1t thịt vi\xean nướng trong nước d\xf9ng lạnh, một đĩa b\xfan v\xe0 sự kết hợp của nhiều loại rau xanh tươi như l\xe1 t\xeda t\xf4, x\xe0 l\xe1ch, rau m\xf9i v\xe0 rau muống","southFood":"10 m\xf3n ăn Nam Bộ","southFoodLink":"https://www.tasteatlas.com/most-popular-food-in-southern-vietnam","southFood1":"Kẹo dừa","southFood1Explain":"Kẹo dừa l\xe0 một loại b\xe1nh kẹo truyền thống của Việt Nam c\xf3 nguồn gốc từ Bến Tre, nơi nổi tiếng với tr\xe1i dừa. Loại kẹo mềm v\xe0 dai n\xe0y được l\xe0m từ sự kết hợp của c\xf9i dừa nạo, nước cốt dừa, kem dừa, đường v\xe0 thường l\xe0 xi-r\xf4 mạch nha.","southFood2":"Hủ tiếu Nam Vang","southFood2Explain":"M\xf3n ăn đường phố nổi tiếng của Việt Nam với nước d\xf9ng trong v\xe0 đậm đ\xe0 được ninh từ xương heo, thịt heo, mực kh\xf4 v\xe0 t\xf4m.","southFood3":"B\xe1nh cam","southFood3Explain":"B\xe1nh cam giống như b\xe1nh r\xe1n truyền thống với lớp men caramel b\xean tr\xean v\xe0 nh\xe2n đậu xanh. Nếu được chế biến đ\xfang c\xe1ch, b\xe1nh r\xe1n sẽ gi\xf2n b\xean ngo\xe0i v\xe0 mềm, dai v\xe0 đặc b\xean trong.","southFood4":"B\xe1nh khọt","southFood4Explain":"B\xe1nh khọt l\xe0 một loại b\xe1nh mặn của Việt Nam được l\xe0m bằng bột gạo mỏng v\xe0 nấu tr\xean vỉ nướng ngo\xe0i trời được trang bị khu\xf4n b\xe1nh khọt chuy\xean dụng bằng đất nung. Bột được đổ v\xe0o khu\xf4n, v\xe0 b\xe1nh kếp sau đ\xf3 được phủ l\xean tr\xean với nhiều nguy\xean liệu mặn kh\xe1c nhau.","southFood5":"B\xe1nh m\xec đặc biệt","southFood5Explain":"B\xe1nh m\xec đặc biệt l\xe0 một loại b\xe1nh m\xec truyền thống của Việt Nam c\xf3 nguồn gốc từ S\xe0i G\xf2n (Th\xe0nh phố Hồ Ch\xed Minh). B\xe1nh m\xec sandwich thường bao gồm một chiếc b\xe1nh m\xec baguette chia đ\xf4i chứa đầy pate gan heo v\xe0 thịt nguội cắt l\xe1t mỏng như headcheese, gi\xf2 lụa v\xe0 gi\xf2 heo. Tiếp theo, b\xe1nh m\xec baguette được l\xe0m đầy với sốt mayonnaise, c\xe0 rốt v\xe0 củ cải muối, ớt th\xe1i l\xe1t, dưa chuột th\xe1i sợi v\xe0 rau m\xf9i.","southFood6":"B\xfan mắm","southFood6Explain":"B\xfan mắm l\xe0 một m\xf3n ăn truyền thống của Việt Nam c\xf3 nguồn gốc từ tỉnh S\xf3c Trăng. M\xf3n ăn bao gồm b\xfan b\xfan, nước mắm c\xf3 vị c\xe1 đục, mặn v\xe0 l\xean men, mực, t\xf4m, c\xe1 da trơn, c\xe0 t\xedm (ng\xe2m nước d\xf9ng), chả c\xe1 v\xe0 thịt ba chỉ quay.","southFood7":"Cơm tấm","southFood7Explain":"Cơm tấm l\xe0 một m\xf3n ăn truyền thống của Việt Nam thường được b\xe1n dưới dạng thức ăn đường phố. N\xf3 bao gồm những hạt gạo bị vỡ v\xe0 kh\xf4ng ho\xe0n hảo m\xe0 theo truyền thống được bỏ đi sau qu\xe1 tr\xecnh xay x\xe1t, nhưng ng\xe0y nay n\xf3 l\xe0 một m\xf3n ăn đặc trưng của Th\xe0nh phố Hồ Ch\xed Minh.","southFood8":"Canh chua","southFood8Explain":"Canh chua l\xe0 m\xf3n canh phổ biến của Việt Nam c\xf3 nguồn gốc từ v\xf9ng đồng bằng s\xf4ng Cửu Long. N\xf3 được đặc trưng bởi hương vị tương phản - chua, ngọt v\xe0 mặn. Nước lẩu c\xf3 sự kết hợp của c\xe1c nguy\xean liệu như hải sản (lươn, t\xf4m, c\xe1 l\xf3c, c\xe1 tr\xea), c\xe0 chua, đậu bắp, thơm, me, gi\xe1 đỗ v\xe0 c\xe1c loại rau thơm với hương vị thơm nồng.","southFood9":"Chả gi\xf2","southFood9Explain":"Miền nam gọi l\xe0 chả gi\xf2, miền bắc gọi l\xe0 nem r\xe1n, cả hai t\xean đều được d\xf9ng để chỉ c\xf9ng một m\xf3n ăn - nem r\xe1n. Đặc điểm ch\xednh của m\xf3n b\xe1nh cuốn thơm ngon n\xe0y l\xe0 nh\xe2n thịt heo v\xe0 t\xf4m, được cuốn trong lớp b\xe1nh tr\xe1ng mỏng manh.","southFood10":"B\xfan b\xf2 nam bộ","southFood10Explain":"Nguy\xean liệu ch\xednh bao gồm thịt b\xf2, b\xfan, tỏi, rau thơm (ng\xf2, bạc h\xe0) v\xe0 c\xe1c loại rau (c\xe0 rốt, dưa chuột, x\xe0 l\xe1ch, gi\xe1 đỗ), c\xf2n đậu phộng rang v\xe0 h\xe0nh kh\xf4 th\xec ho\xe0n to\xe0n kh\xf4ng bắt buộc.","centralFood":"10 m\xf3n ăn miền Trung","centralFoodLink":"https://www.tasteatlas.com/most-popular-food-in-central-vietnam","centralFood1":"B\xe1nh hỏi","centralFood1Explain":"Sợi hủ tiếu Việt Nam cực kỳ mỏng được dệt th\xe0nh những b\xf3 h\xecnh chữ nhật phẳng theo kiểu truyền thống.","centralFood2":"Mực một nắng","centralFood2Explain":"Mực một nắng l\xe0 m\xf3n ăn truyền thống của Việt Nam c\xf3 nguồn gốc từ Phan Thiết. Đặc sản hải sản n\xe0y bao gồm mực kh\xf4. Những con mực tươi, to được phơi nắng trong một ng\xe0y rồi nướng tr\xean than hồng. Nếu nhiệt qu\xe1 cao, mực sẽ bị ch\xedn qu\xe1, cứng b\xean ngo\xe0i nhưng hiếm b\xean trong.","centralFood3":"Cao lầu","centralFood3Explain":"Cao lầu l\xe0 một m\xf3n ăn đặc trưng của Hội An bao gồm m\xec l\xe0m từ gạo tươi trồng tại địa phương, dung dịch kiềm l\xe0m từ tro của c\xe2y địa phương v\xe0 nước từ giếng địa phương (B\xe1 Lễ). C\xf9ng với hủ tiếu, một b\xe1t cao lầu cũng bao gồm thịt lợn quay th\xe1i l\xe1t mỏng (x\xe1 siu), rau xanh, gi\xe1 đỗ, b\xe1nh m\xec nướng được l\xe0m từ c\xf9ng nguy\xean liệu như hủ tiếu, v\xe0 chỉ một ch\xfat nước d\xf9ng c\xf3 hương vị.","centralFood4":"B\xe1nh x\xe8o Huế (B\xe1nh kho\xe1i)","centralFood4Explain":"B\xe1nh kho\xe1i l\xe0 một loại b\xe1nh truyền thống của Việt Nam c\xf3 nguồn gốc từ Huế. Bột thường được l\xe0m bằng sự kết hợp của bột gạo, nước, trứng, đường v\xe0 muối. N\xf3 được chi\xean trong chảo v\xe0 cho nấm th\xe1i l\xe1t, thịt lợn, t\xf4m, gi\xe1 đỗ, c\xe0 rốt, x\xfac x\xedch Việt Nam v\xe0 h\xe0nh l\xe1 l\xean tr\xean, sau đ\xf3 chi\xean th\xeam cho đến khi gi\xf2n v\xe0 c\xf3 m\xe0u v\xe0ng n\xe2u.","centralFood5":"Nem nướng","centralFood5Explain":"Nem nướng l\xe0 m\xf3n x\xfac x\xedch nướng truyền thống của Việt Nam c\xf3 nguồn gốc từ Nha Trang. X\xfac x\xedch thường được l\xe0m với sự kết hợp của mỡ lợn xay v\xe0 thịt, tỏi, nước mắm, hẹ, đường, hạt ti\xeau đen v\xe0 thường l\xe0 baking soda d\xf9ng để l\xe0m phồng thịt.","centralFood6":"B\xe1nh bột lọc","centralFood6Explain":"Những chiếc b\xe1nh bao Việt Nam trong mờ n\xe0y thường được chế biến với lớp vỏ l\xe0m từ bột sắn v\xe0 nh\xe2n thường kết hợp giữa t\xf4m v\xe0 thịt lợn b\xe9o. B\xe1nh bao c\xf3 thể được nấu ch\xedn hoặc g\xf3i trong l\xe1 chuối rồi đem hấp. Theo truyền thống, ch\xfang được phục vụ với nước chấm l\xe0m từ c\xe1 ở b\xean cạnh.","centralFood7":"B\xe1nh b\xe8o","centralFood7Explain":"B\xe1nh b\xe8o l\xe0 một loại b\xe1nh hấp phổ biến của Việt Nam bao gồm c\xe1c th\xe0nh phần ch\xednh như bột gạo, nước mắm ớt xanh v\xe0 t\xf4m hoặc thịt lợn. Ngo\xe0i ra, c\xf3 thể cho th\xeam b\xfan, lạc rang hoặc h\xe0nh phi v\xe0o b\xe1nh để tăng hương vị.","centralFood8":"M\xec Quảng","centralFood8Explain":"Một sự kết hợp của m\xec gạo phẳng v\xe0 m\xec trứng m\xe0u v\xe0ng được ăn k\xe8m với một \xedt nước d\xf9ng thịt c\xf3 hương vị v\xe0 c\xe1c loại rau v\xe0 rau thơm tươi gi\xf2n như hoa chuối, x\xe0 l\xe1ch, rau m\xf9i, h\xe0nh l\xe1 v\xe0 rau m\xf9i Việt Nam.","centralFood9":"B\xfan b\xf2 Huế","centralFood9Explain":"B\xfan b\xf2 Huế l\xe0 một m\xf3n canh chủ yếu của Việt Nam được d\xf9ng v\xe0o bữa s\xe1ng theo truyền thống, bao gồm nước d\xf9ng từ xương heo v\xe0 xương b\xf2, b\xfan, sả, mắm t\xf4m, nước cốt chanh v\xe0 nhiều loại rau thơm. M\xf3n s\xfap cay hơn nhiều so với hầu hết c\xe1c m\xf3n s\xfap Việt Nam v\xe0 hương vị của n\xf3 thường được m\xf4 tả l\xe0 đậm đ\xe0 v\xe0 phức tạp.","centralFood10":"B\xe1nh X\xe8o","centralFood10Explain":"Một chiếc b\xe1nh gạo gi\xf2n, nhồi. C\xe1i t\xean n\xe0y đề cập đến \xe2m thanh m\xe0 một lớp bột gạo mỏng tạo ra khi n\xf3 được đổ v\xe0o chảo n\xf3ng. Đ\xf3 l\xe0 một loại b\xe1nh chi\xean mặn l\xe0m từ bột gạo, nước v\xe0 bột nghệ."}')}}]);